mapper.select:
select
from t_favorite a left join t_click_rate b on a.app_id=b.app_id LEFT JOIN (select a.* from t_status a where not exists(select 1 from t_status b where b.app_id=a.app_id and b.create_by=a.create_by and b.create_date>a.create_date) and a.create_by=#{userId} and a.del_flag='0') c on c.app_id=a.app_id and c.create_by=a.create_by where 1=1
and a.create_by=#{userId}
and a.update_by=#{updateBy}
and a.del_flag="0" order by b.click_rate desc

select count(*) from t_favorite
where 1=1 and del_flag=0

	and app_id=#{appId}



	and create_by=#{createBy}
